mapper.select:
select order_id orderId, b.v originProvinceName, c.v originCityName, origin, d.v destinationProvinceName, e.v destinationCityName, destination, order_state orderState, postage, f.account, f.phone, bag_id bagId, a.gmt_create gmtCreate from t_sys_order a,t_sys_dictionary b,t_sys_dictionary c,t_sys_dictionary d,t_sys_dictionary e,t_sys_user f where a.is_deleted = 0 and origin_province_id = b.dictionary_id and origin_city_id = c.dictionary_id and destination_province_id = d.dictionary_id and destination_city_id = e.dictionary_id and a.user_id = f.user_id
and user_id = #{userId}
and order_id = #{orderId}
and f.account like concat('%',#{account},'%')
and f.phone like concat('%',#{phone},'%')
and order_state = #{orderState}
and a.gmt_create between #{payTimeStart} and #{payTimeEnd}
order by a.gmt_create desc
