mapper.select:
select a.order_id orderId, b.line_name lineName, c.site_name originSiteName, d.site_name destinationSiteName, a.receiver_name receiverName, a.receiver_phone receiverPhone, weight, postage, order_state orderState, bag_id bagId, e.account, e.name, e.phone, concat(g.v,h.v,f.site_name) origin, concat(j.v,k.v,i.site_name) destination, a.gmt_create gmtCreate from t_sys_order a,t_sys_line b,t_sys_site c,t_sys_site d,t_sys_user e, t_sys_site f,t_sys_dictionary g,t_sys_dictionary h, t_sys_site i,t_sys_dictionary j,t_sys_dictionary k where a.is_deleted = 0 and a.line_id = b.line_id and a.origin_site_id = c.site_id and a.destination_site_id = d.site_id and a.user_id = e.user_id and a.origin_site_id = f.site_id and f.province_id = g.dictionary_id and f.city_id = h.dictionary_id and a.destination_site_id = i.site_id and i.province_id = j.dictionary_id and i.city_id = k.dictionary_id
and a.user_id = #{userId}
and order_id = #{orderId}
and e.account like concat('%',#{account},'%')
and e.phone like concat('%',#{phone},'%')
and order_state = #{orderState}
and a.gmt_create between #{payTimeStart} and #{payTimeEnd}
order by a.gmt_create desc
