mapper.select:
select
    a.site_id siteId,
    a.site_name siteName,
    b.account
from
    t_sys_site a left join t_sys_user b
on
    a.is_deleted = 0
    and b.is_deleted = 0
    and b.site_id = a.site_id

    and site_id = #{siteId}


    and site_name = #{siteName}


    and account = #{account}


    and province_id = #{provinceId}


    and city_id = #{cityId}

order by a.gmt_create desc
